import React, { Component } from 'react';
import { Container, Row, Col, Image } from 'react-bootstrap';

class MusicList extends Component {


    render(){
        const musicas = Object.values(this.props.music);

        return (
            <div className="" style={containMusic}>
                <Container className="pt-3">
                    <div className="text-center my-5">
                        <p style={{color: "#EE4622", fontSize: 24, fontWeight: 20}}> Dengarkan Dan Rasakan </p>
                    </div>
                    <div className="text-light" id="genre-music"></div>
                    <Row>
                        {   
                            musicas.slice(6).map( (x, index) => {
                                return (
                                            <Col xs={5} md={2} lg={2} className="" key={index} >
                                                <div style={{background: "#3A3A3A"}} className="rounded text-center pt-2 px-2"> 
                                                        <Image src={`http://localhost:5000/public/${x.thumbnail}`} style={setGambar} onClick={() => this.props.setPlayIndex(index)}  /> 
                                                    <div className="pt-1">
                                                        <div className="d-inline-flex">
                                                            <p className="text-white text-font-weight-bold text-left" style={{overflow: "hidden", width: 111, fontSize: 14}}>
                                                                {x.title.substring(0,14)}
                                                            </p>
                                                            <p className="text-white text-font-weight-light text-right" style={{fontSize: 14}}>
                                                                {x.year}
                                                            </p>
                                                        </div>
                                                        <p className="text-font-weight-light text-left pb-2 text-white" style={{fontSize: 14}} >{x.Artist.name}</p>
                                                    </div>
                                                </div>
                                            </Col>
                                        )
                            })
                        }
                    </Row>
                    <hr className="bg-white" />
                </Container>
            </div>
        );
    }
}

const containMusic = {
    height: "100%",
    width: "100%",
    marginTop: "-33px", 
    background: "black", 
    boxShadow: "-6px -42px 69px -8px rgba(0,0,0,0.75)",
    paddingBottom: 50,
}

const setGambar = {
    border: '2px solid white',
    width: '130px',
    heiight: '130px'
} 

export default MusicList;
