import React, { Component } from 'react';
import { Jumbotron, Container, Image, Form, Button, Row, Col } from 'react-bootstrap';
import ReactPlayer from 'react-player';

// import theWitcherBanner from './data/main_banner.png';
// import theJoker from './data/movies.png';
// import theLacasa from './data/lacasa_banner.png';

// import theWitcherTitle from './data/thw.png';
// import theJokerTitle from './data/joker.png';
// import theLacasaTitle from './data/lacasa.png';

import MOVIES from './data/data';




class Banner extends Component {

    constructor(props){
        super(props);
        this.state = {
            onKlik: false
        }
    }

    onTest(){
        this.setState({onKlik: !this.state.onKlik});
    }

    render(){

        let { ids } = this.props;

        return (
        <div style={{background: 'black'}}>
            <a onClick={() => this.onTest()}><div style={{background: 'rgba(31, 31, 31, 0.3)', zIndex: 999, height: 540, width: '1680px', position: 'absolute' }}></div></a>
            <Container style={{height: 570}} className="py-3">
                <Row>
                    <Col xs={12} md={12}>
                        <ReactPlayer url={MOVIES[ids].video} playing={this.state.onKlik}  width='100%' height='500px'/>
                    </Col>
                </Row>
            </Container>

            <div className="tes" style={{backgroundColor: 'black', marginTop: '-34px'}}>
            <Container className="py-5">
                <Row>
                    <Col xs={6} md={7}>
                        <Row>
                            <Col xs={12} md={4}>
                                <Image src={MOVIES[ids].image} />
                            </Col>
                            <Col xs={12} md={8}>
                                <h1 className='text-white'>{MOVIES[ids].title}</h1>
                                <div className='pb-3 text-white'>
                                    <span className='pr-3'>{MOVIES[ids].year}</span><span> {MOVIES[ids].genre}</span>
                                </div>
                                <h6 className="text-justify text-white font-weight-light" >{MOVIES[ids].deskripsi}</h6>
                            </Col>
                        </Row>

                    </Col>
                    <Col xs={6} md={5} >
                        <div style={{border: '2px solid white', height: '100vh'}}>
                        <ReactPlayer url={MOVIES[ids].video} playing={false} width='100%' height='100%'/>
                        </div>
                    </Col>
                </Row>
            </Container>
                
            </div>
        </div>
        );
    }
}


export default Banner;
