import React, { Component } from 'react';
import Jumbotron from './components/beranda/jumbotron';
import MusicList from './components/beranda-musics/musicList';
import MusicPlayer from './components/beranda-musics/musicPlayer';

import { connect } from "react-redux";
import { getMusics } from "./components/redux/actions/musics";

class App extends Component {

  componentDidMount(){
    this.props.getMusics()
}

  constructor(){
    super()
    this.state = {
        playIndex: 0
    }
}

  setPlayIndex(e){
    this.setState({state: e})
  }

  render(){
    let unlock = localStorage.getItem('role') === 'admin' ? true : false;

    return (
      <div>

      {
          <Jumbotron ids={1} />
      }
      {
          unlock ? 
          ( <>
            <MusicList music={this.props.music} setPlayIndex={this.setPlayIndex} playIndex={this.state.playIndex} />
            <MusicPlayer music={this.props.music} setPlayIndex={this.setPlayIndex} playIndex={this.state.playIndex} />
            </>
          ) : 
          (<MusicList ids={1} homi={"homes"} />)    
      }

      </div>
    );
  }
}


const mapStateToProps = (state) => {
  return {
      music: state.music
  };
};

export default connect(mapStateToProps, { getMusics })(App);
