import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';

//Redux
import { Provider } from 'react-redux';
import store from './components/redux/store';

import App from './App';
import Header from './components/beranda/header';
import Detail from './components/beranda-mdetail/detail';
import PrivateRoute from "./components/private/privateRoute";

import * as serviceWorker from './serviceWorker';

function Routes(){

  const [isLogin, setLogin] = useState(false);

  // const [user, setuUser] = useState({
  //   id: "",
  //   name: "",
  //   email: "",
  //   role: "",
  // });

  const [modalLogin, setModalLogin] = useState(false);
  const [modalRegister, setModalRegister] = useState(false);
  const [error, setError] = useState("");

  const showModalLogin = () => {
    setModalLogin(!modalLogin);
    setModalRegister(false);
  };

// localStorage.setItem('login', true )
// localStorage.setItem('logout', false )
// let login = localStorage.getItem('login');
// let logout = localStorage.getItem('logout');


  const showModalRegister = () => {
    setModalRegister(!modalRegister);
    setModalLogin(false);
  };

  const handleLogout = () => {
    setLogin(false);
  };

  const userLogin = [
    {
      id: 1,
      name: "Ekky",
      email: "ekky@gmail.com",
      password: "admin",
      role: "admin",
    },
    {
      id: 2,
      name: "User Biasa",
      email: "user@gmail.com",
      password: "user",
      role: "user",
    },
  ];

  const handleLogin = (payload) => {
    const isValid = userLogin.filter(
      (user) => user.email == payload.email && user.password == payload.password
    );

    if (isValid.length) {
      setLogin(true);
      setModalLogin(false);
    } else {
      setError("invalid email and password");
      console.log("invalid email and password");
    }
  };

  return(
    <Provider store={store}>
      <div>
          <Router>
            <Header 
              isLogin={isLogin}
              handleLogin={handleLogin}
            />
          <Switch>
            <Route exact path="/" component={App} />
              <PrivateRoute
                exact
                path="/detail/:id/:genre/:status"
                component={Detail}
                isLogin={isLogin}
              />
          </Switch>
        </Router>
      </div>
    </Provider>
  )
}


ReactDOM.render(
  <>
    <Routes />
  </>,
  document.getElementById('root')
);

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
