import React, { Component } from 'react';
import { Button, Container, Row, Col, Dropdown, DropdownButton } from 'react-bootstrap';

class ListFilm extends Component {

    
    render(){

        return (
            <Container className='py-5'>
                <Row>
                    <Col md={10} className="d-inline-flex text-white ">
                        <h1 className="pr-5">List Film</h1>
                        <Dropdown >
                            <Dropdown.Toggle variant="outline-light" id="dropdown-basic">
                                <span className="text-white">Category</span>
                            </Dropdown.Toggle>
                            <Dropdown.Menu className="dropdown-list-film-js">
                                <Dropdown.Item eventKey="1">Action</Dropdown.Item>
                                <Dropdown.Item eventKey="2">Drama</Dropdown.Item>
                                <Dropdown.Item eventKey="3">Commedy</Dropdown.Item>
                                <Dropdown.Item eventKey="4">Romance</Dropdown.Item>
                                <Dropdown.Item eventKey="5">Fight</Dropdown.Item>
                            </Dropdown.Menu>
                        </Dropdown>
                    </Col>
                    <Col md={2} className='text-right pt-2'>
                        <Button className="btn btn-outline-danger text-white" style={{background: '#E50914'}} block
                        href={"/add_film/admin/007"}
                        >Add Film</Button>
                    </Col>
                </Row>
            </Container>
        );
    }
}

export default ListFilm;
