import React, { Component } from 'react';
import { Nav, Navbar, Button, Form, Image, Modal, DropdownButton, Dropdown } from 'react-bootstrap';
import { useHistory } from "react-router-dom";

import logoImage from './data/logo.png';
import logoUser from './data/user_photo.png';


// Modal function 
function LoginUser(props) {

    const history = useHistory();

    function tester (){
        let path = `/`; 
        history.push(path);
    }

    return (
    <Modal
        {...props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
        
    >

        <Modal.Body className="bg-dark rounded pb-4">
            <h2 className="bg-dark text-light d-flex justify-content-center mb-5" >Log Out</h2>
            <Form style={{width:"50%", margin:"auto"}}>
                <p className="text-white">Are you sure to leave right now ? </p>
                <Button type="submit" variant="danger" block className="text-light" onClick={() => tester()} >
                    Yes
                </Button>
                <div className="text-right mt-2">
                <Button onClick={props.onHide} block >No</Button>
                </div>
            </Form>
        </Modal.Body>
        
    </Modal>
    );
}


function Registered(props) {
    return (
        <Modal
            {...props}
            size="lg"
            aria-labelledby="contained-modal-title-vcenter"
            

            >
        
            <Modal.Body className="bg-dark rounded pb-4" >
                <h1 className="bg-dark text-light d-flex justify-content-center" >Register</h1>
                <Form style={{width:"50%", margin:"auto"}} method="get">
                    <Form.Group controlId="formBasicEmail">
                        <Form.Label className="text-light">Email address</Form.Label>
                        <Form.Control type="email" placeholder="Enter email" className="bg-light"/>
                        <Form.Text className="text-light">
                        * We'll never share your email with anyone else.
                        </Form.Text>
                    </Form.Group>
                    
                    <Form.Group controlId="formBasicPassword">
                        <Form.Label className="text-light">Password</Form.Label>
                        <Form.Control type="password" placeholder="Password" />
                    </Form.Group>
                    
                    <Form.Group controlId="formBasicName">
                        <Form.Label className="text-light">Full Name</Form.Label>
                        <Form.Control type="text" placeholder="Full Name" />
                    </Form.Group>
                    
                    <Form.Group controlId="exampleForm.SelectCustom">
                        <Form.Label className="text-light">Gender</Form.Label>
                        <Form.Control as="select" custom>
                        <option>Male</option>
                        <option>Female</option>
                        </Form.Control>
                    </Form.Group>
                    
                    <Form.Group controlId="formBasicPhone">
                        <Form.Label className="text-light">Phone</Form.Label>
                        <Form.Control type="text" placeholder="Phone" />
                    </Form.Group>
                    
                    <Form.Group controlId="formBasicAddress">
                        <Form.Label className="text-light">Address</Form.Label>
                        <Form.Control type="text" placeholder="Address" />
                    </Form.Group>
                    
                    <Button type="submit" variant="light" size="lg" className="text-danger" block>
                        Register
                    </Button>
                    <p className="text-light">don't have an account ? just <a href="#test">click here</a></p>
                    <div className="text-right mt-2">
                    <Button onClick={props.onHide} >Close</Button>
                    </div>
                </Form>
            </Modal.Body>
        
        </Modal>

        
    );
}



// END of Modal function

class HeaderLogin extends Component {

    constructor(){
        super()
        this.state = {
            modalShow: false, modalRegister: false
        }
    }

    render(){

        const { modalShow, modalRegister} = this.state;

        return (
        <>
            <Navbar bg="dark" expand="lg">
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav">
                <Nav className="mr-auto">
                <Nav.Link href="#home" ><span className="text-light">Home</span></Nav.Link>
                <Nav.Link href="#tv-show"><span className="text-light">TV Shows</span></Nav.Link>
                <Nav.Link href="#movies"><span className="text-light">Movies</span></Nav.Link>
                </Nav>
                
                <Nav style={dumbFlix}>
                <Image src={logoImage} fluid />
                </Nav>
                
                <Form inline>

                    <div>
                        
                        <DropdownButton
                        alignRight
                        title={<Image src={logoUser} fluid />}
                        id="dropdown-menu-align-right"
                        variant="dark"
                        >
                            <Dropdown.Item eventKey="1"><i className='fas fa-user' style={{paddingRight:'10px'}}></i> Profile</Dropdown.Item>
                            <Dropdown.Item eventKey="3"><i className='fas fa-money-check' style={{paddingRight:'5px'}}></i> Pay</Dropdown.Item>
                            <Dropdown.Divider />
                            <Dropdown.Item eventKey="4" onClick={() => this.setState({modalShow: true})}><i className='fas fa-sign-out-alt' style={{paddingRight:'10px', color:'red'}}></i>   Log out</Dropdown.Item>

                        </DropdownButton>
                    </div>
                </Form>
            </Navbar.Collapse>
            </Navbar>

            <LoginUser
                show={modalShow}
                onHide={() => this.setState({modalShow: false})}
                nilai={1}
            />

            <Registered
                show={modalRegister}
                onHide={() => this.setState({modalRegister: false})}
            />
        </>
        );
    }
}


const dumbFlix = {
    marginRight: 'auto'
};

const triangle = {
    position: 'absolute',
	width: 0,
	height: 0,
    marginTop: '1px',
    marginLeft: '1',
	borderLeft: '15px solid transparent',
	borderRight: '15px solid transparent',
	borderBottom: '25px solid #555',
    backgroundColor: 'transparent'
};


export default HeaderLogin;
