import React, { Component } from 'react';
import { Button, Container, Row, Col, Dropdown, DropdownButton } from 'react-bootstrap';

import Admin from './admin';
import List from './list';

class App extends Component {

    constructor(){
        super()
        this.state = {
            choose: false
        }

    }
    
    test(){
        this.setState({choose: !this.state.choose})
    }

    render(){
        let { user }  = this.props.match.params;
        const { choose } = this.state;

        return (
        <div style={{background: 'black'}}>
            <Admin userX={user} pathX={() => this.test()}/>
            {
                choose ? (<List />) : (
                <div> 

                    <Container>
                        <Row>
                            <Col md-6 className="d-inline-flex">
                                <h4>List Film</h4>
                                <DropdownButton
                                alignRight
                                title={<Button className="btn btn-danger">Category</Button>}
                                id="dropdown-menu-align-right"
                                variant='dark'
                                >
                                    <Dropdown.Item eventKey="1">Action</Dropdown.Item>
                                    <Dropdown.Item eventKey="2">Another action</Dropdown.Item>
                                    <Dropdown.Item eventKey="3">Something else here</Dropdown.Item>
                                    <Dropdown.Divider />
                                    <Dropdown.Item eventKey="4">Separated link</Dropdown.Item>
                                </DropdownButton>
                            </Col>
                            <Col md-6 className='text-right'>
                                <Button className="btn btn-danger">Add Film</Button>
                            </Col>
                        </Row>
                    </Container>

                </div>
                )
            }
        </div>
        );
    }
}

export default App;
