import React from 'react';
import ReactJkMusicPlayer from 'react-jinke-music-player';
import './musicPlayer.css';

const MusicPlayer = ({ music, playIndex, setPlayIndex }) => {
    // console.log(playIndex)
    const musicas = Object.values(music);
	const playlist = musicas.map((X) => ({
		name: X.title,
		singer: X.Artist.name,
		cover: `http://localhost:5000/public/${X.thumbnail}`,
		musicSrc: X.attach
	}));

	return (
		<div style={{ height: '100px', width: '100%' }}>
			<ReactJkMusicPlayer
				mode="full"
				audioLists={playlist}
				defaultPlayIndex={0}
				autoPlay={false}
				showDownload={false}
				showThemeSwitch={false}
				toggleMode={false}
				responsive={false}
				glassBg={true}
				playIndex={playIndex}
				onAudioPlay={(audioInfo) => {
					if (playIndex === audioInfo.playIndex) {
						return;
					}
					setPlayIndex(audioInfo.playIndex);
				}}
			/> 
		</div>
	);
};

export default MusicPlayer;
