import React, { Component } from 'react';
import { Jumbotron, Container, Image, Form, Button, Row, Col } from 'react-bootstrap';
import ReactPlayer from 'react-player';

import theWitcherBanner from './data/main_banner.png';
import theJoker from './data/movies.png';
import theLacasa from './data/lacasa_banner.png';

import theWitcherTitle from './data/thw.png';
import theJokerTitle from './data/joker.png';
import theLacasaTitle from './data/lacasa.png';

import MOVIES from './data/data';




class Banner extends Component {

    constructor(props){
        super(props);
        this.state = {
            onKlik: false
        }
    }

    onTest(){
        this.setState({onKlik: !this.state.onKlik});
    }

    render(){

        let { ids } = this.props;

        return (
        <div style={{background: 'black'}}>
            <a onClick={() => this.onTest()}><div style={{background: 'rgba(31, 31, 31, 0.3)', zIndex: 999, height: 540, width: '1680px', position: 'absolute' }}></div></a>
            <Container style={{height: 570}} className="py-3">
                <Row>
                    <Col xs={12} md={12}>
                        <ReactPlayer url={MOVIES[ids].video} playing={this.state.onKlik}  width='100%' height='500px'/>
                    </Col>
                </Row>
            </Container>

            <div className="tes" style={{backgroundColor: 'black', marginTop: '-34px'}}>
            <Container className="py-5">
                <Row>
                    <Col xs={6} md={7}>
                        <Row>
                            <Col xs={12} md={4}>
                                <Image src={MOVIES[ids].image} />
                            </Col>
                            <Col xs={12} md={8}>
                                <h1 className='text-white'>Hello</h1>
                                <div className='pb-3 text-white'>
                                    <span className='pr-3'>Year</span><span> genre cuy</span>
                                </div>
                                <h6 className="text-justify text-white font-weight-light" >loremadjn adjnakjd nakdjn akcnjcjnakcn 
                                    askcnak jcnak cnaknca ascjan ckanckajc nakcnak cnakcnakca kjcnakjcn akckanckanc ajcz jxvbzvnb 
                                    iajknvaovnlkxnvlzv noavnkxzv,loremadjn adjnakjd nakdjn akcnjcjnakcn askcnak jcnak cnaknca ascjan
                                    ckanckajc nakcnak cnakcnakca kjcnakjcn akckanckanc ajcz jxvbzvnb iajknvaovnlkxnvlzv noavnkxzv
                                </h6>
                            </Col>
                        </Row>

                    </Col>
                    <Col xs={6} md={5}>
                        <ReactPlayer url='https://www.youtube.com/watch?v=ysz5S6PUM-U' playing={false} width='100%' height='100%'/>
                    </Col>
                </Row>
            </Container>
                
            </div>
        </div>
        );
    }
}

const JUDUL = [{
    id: 1,
    image: theWitcherTitle,
    text: 'Geralt of Rivia, a solitary monster hunter, struggles to find his place in a world where people often prove more wicked than beast',
    genre: 'Tv Series',
    year: '2019'
    },{
    id: 2,
    image: theJokerTitle,
    text: 'Joker , a solitary villain, struggles to find his place in the world a world where people often prove more wicked than beast',
    genre: 'Movies',
    year: '2019' 
    },{
    id: 3,
    image: theLacasaTitle,
    text: 'Money Heist is a crime drama on Netflix - originally called La Casa de Papel. Money Heist season 3 just been released by the streaming service the plot reads: "Eight thieves take hostages and lock themselves in the royal Mint of Spain as a criminal mastermind manipulates the police to carry out his plan',
    genre: 'Tv Series',
    year: '2017' 
    }
];


const GAMBAR_COVER = [{
    id: 1,
    image: theWitcherBanner
    },{
    id: 2,
    image: theJoker
    },{
    id: 3,
    image: theLacasa 
    }
];

const textWitcher = {
    color: "white",
    textShadow: "2px 2px 2px #000",
    marginTop: "15px",
    marginBottom: "15px"
};

const buttonWatch = {
    width: '200px',
    background: '#E50914',
    borderRadius: '5px',
    
};

export default Banner;
