import { MUSICS } from "../constants/action-types";
import { API } from "../config/api";


export const getMusics = () => {
    return {
        type: MUSICS,
        payload: async () => {
        try {
            const {
            data: { data },
            } = await API.get("/musics");
            
            return data;
        } catch (error) {
            if (error.response) {
            const { data, status } = error.response;

            if (status > 399) throw data.error;
            }
        }
        },
    };
};
