    import React, { Component } from 'react';
    import CUSTOMER from './customer/customers';

    import { Container,Row,Col } from 'react-bootstrap';

    class App extends Component {
    render(){
        return (
        <div className="pb-5">
        <Container className='mt-5 text-left text-white font-weight-bold' style={{width: '100vh', height: 20, marginLeft:'15%'}}>
            <Row>
                <Col>
                    <h5>Incoming Transaction</h5>
                </Col>
            </Row>
        </Container>
        <Container className='my-5 bg-dark text-white' style={{width: 900, height: 400}}>
            <Row style={{fontSize: 14, color: '#E50914'}} className="py-2">
                <Col md={1}>
                No
                </Col>
                <Col md={2}>
                Users
                </Col>
                <Col md={2}>
                Bukti Transfer
                </Col>
                <Col md={2}>
                Remaining Active
                </Col>
                <Col md={2}>
                Status User
                </Col>
                <Col md={2}>
                Status payment
                </Col>
                <Col md={1}>
                Action
                </Col>
            </Row>
            {
                CUSTOMER.map( customer => { 
                let statusX = customer.status
                let paymentX = customer.payment
                let actionX = customer.action
                let color = '#FF0742'
                let colorPay = '#FF0742'
                let colorAct = '#FF0742'

                if ( statusX === 'Active' ){
                    color = '#0ACF83'
                }

                if ( paymentX === 'Active' ){
                    colorPay = '#0ACF83'
                }else if ( paymentX === 'Pending'){
                    colorPay = '#F7941E'
                }

                if ( actionX === 'Active' ){
                    colorAct = '#1C9CD2'
                }


            return <div key={customer.id}>
                <hr style={{ height: 1, backgroundColor: '#7b7c78',width: ' 100%' }}/>
                <Row style={{fontSize: 14}} className="py-2">
                    <Col md={1}>
                        {customer.id}
                    </Col>
                    <Col md={2}>
                        {customer.users}
                    </Col>
                    <Col md={2}>
                        {customer.bukti}
                    </Col>
                    <Col md={2}>
                        {customer.remaining}
                    </Col>
                    <Col md={2} style={{color: `${color}`, fontWeight: 'bold'}}>
                        {customer.status}
                    </Col>
                    <Col md={2} style={{color: `${colorPay}`, fontWeight: 'bold'}}>
                        {customer.payment}
                    </Col>
                    <Col md={1}>
                        <i className='fas fa-caret-down' style={{color: `${colorAct}`, fontSize: 18}}></i>
                    </Col>
                </Row>
            </div>
                })
            }
        </Container>
    </div>        
        );
    }
    }

    export default App;
