class Header extends Component {

    constructor(){
        super()
        this.state = {
            modalShow: false, modalRegister: false, id: 0, homes: "homes"
        }

    }


    testX(e, d){
        this.setState({id: e});
        this.setState({homes: d});
    }

    testY(f, g){
        this.setState({homes: f});
        this.setState({id: g});
    }

    render(){   

        const { modalShow, modalRegister, id, homes} = this.state;
        const { isLogin, showModalLogin, handleLogin, error } = this.props;
    
        return (
        <>
            <Navbar bg="dark" expand="lg">
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav">
                <Nav className="mr-auto">
                <Nav.Link href="#" onClick={() => this.testY('homes', 0)}><span className="text-light">Home</span></Nav.Link>
                <Nav.Link href="#" onClick={() => this.testX(2, 'x')}><span className="text-light">TV Shows</span></Nav.Link>
                <Nav.Link href="#" onClick={() => this.testX(1, 'x')}><span className="text-light">Movies</span></Nav.Link>
                </Nav>
                
                <Nav style={dumbFlix}>
                <Image src={logoImage} fluid />
                </Nav>
                
                <Form inline>

                    { !isLogin && (<div>
                        <Button variant="btn btn-light mr-3 text-danger" style={{width:100}} onClick={() => this.setState({modalRegister:true})}>Register</Button>
                        <Button variant="btn btn-danger" style={{width:100}} onClick={() => this.setState({modalShow:true})}>Login</Button>
                    </div>)}
                    { isLogin && (
                        <div>
                        <DropdownButton
                        alignRight
                        title="user"
                        id="dropdown-menu-align-right"
                        variant="dark"
                        >
                            <Dropdown.Item eventKey="3" > <i className='fas fa-file-video' style={{width: 20}}></i> List Film </Dropdown.Item>
                            <Dropdown.Item eventKey="4" > <i className='fas fa-coins' style={{width: 20}}></i> Transaction </Dropdown.Item>
                            <Dropdown.Divider />
                            <Dropdown.Item eventKey="5"> <i className='fas fa-sign-out-alt pr-2' style={{color: 'red'}}></i> Logout</Dropdown.Item>
                        </DropdownButton>
                    </div>
                    )}

                </Form>
            </Navbar.Collapse>
            </Navbar>

            <LoginUser
                show={modalShow}
                onHide={() => this.setState({modalShow: false})}
                smodal={showModalLogin}
                hlogin={handleLogin}
                errors={error}
            />

            <Registered
                show={modalRegister}
                onHide={() => this.setState({modalRegister: false})}
            />
        </>
        );
    }
}


const dumbFlix = {
    marginRight: 'auto'
};


export default Header;
