class App extends Component {

  constructor(){
    super()
    this.state = {
        playIndex: 0
    }
}

  setPlayIndex(e){
    this.setState({state: e})
  }

  render(){
    let unlock = localStorage.getItem('role') === 'admin' ? true : false;

    return (
      <div>

      {
          <Jumbotron ids={1} />
      }
      {
          unlock ? (<MusicList />) : (<MusicList ids={1} homi={"homes"} />)    
      }

      </div>
    );
  }
}

export default App;
