import React, { Component } from 'react';
import Jumbotron from './components/beranda/jumbotron';
import MusicList from './components/beranda-musics/musicList';
import MusicPlayer from './components/beranda-musics/musicPlayer';

import { connect } from "react-redux";
import { getMusics } from "./components/redux/actions/musics";

class App extends Component {

  constructor(){
    super()
    this.state = {
        playIndex: 0
    }
    this.audioInstance = null
}

  setPlayIndex = (e) => {
    this.setState({playIndex: e})
  }


  componentDidMount(){
    this.props.getMusics()
    console.log("Updated audio instance", this.audioInstance);
}

  render(){
    console.log(this.state.playIndex)
    // const { data: dataUser, loading, error } = this.props.auth;
    const { data: listMusic } = this.props.music;
    if (!(Object.keys(listMusic).length === 0 && listMusic.constructor === Object)){
        console.log(listMusic);
    }

    let unlock = localStorage.getItem('role') === 'admin' ? true : false;

    return (
      <div>

      {
          <Jumbotron ids={1} />
      }
      {
          unlock ? 
          ( <>
            <MusicList music={listMusic} setPlayIndex={this.setPlayIndex} playIndex={this.state.playIndex} />
            <MusicPlayer music={listMusic} setPlayIndex={this.setPlayIndex} playIndex={this.state.playIndex} />
            </>
          ) : 
          (<MusicList ids={1} homi={"homes"} />)    
      }

      </div>
    );
  }
}


const mapStateToProps = (state) => {
  return {
      music: state.music
  };
};

export default connect(mapStateToProps, { getMusics })(App);
