import React, { Component } from 'react';
import Jumbotron from './components/beranda/jumbotron';
import MusicList from './components/beranda-musics/musicList';
import { connect } from "react-redux";
import { getMusics } from "./components/redux/actions/musics";
// const Home = ({
// 	getMusicAll,
// 	music: { musicAll },
// 	auth: { isAuthenticated, user, loading }
// }) => {
// 	const [ playIndex, setPlayIndex ] = useState(0);

class App extends Component {

  constructor(){
    super()
    this.state = {
        playIndex: 0
    }
}

  setPlayIndex(e){
    this.setState({state: e})
  }

  render(){
    let unlock = localStorage.getItem('role') === 'admin' ? true : false;

    return (
      <div>

      {
          <Jumbotron ids={1} />
      }
      {
          unlock ? 
          (<MusicList music={this.props.music} getMusics={this.props.getMusics} setPlayIndex={this.setPlayIndex} playIndex={this.state.playIndex} />) : 
          (<MusicList ids={1} homi={"homes"} />)    
      }

      </div>
    );
  }
}


const mapStateToProps = (state) => {
  return {
      music: state.music
  };
};

export default connect(mapStateToProps, { getMusics })(App);
