import React, { Component } from 'react';

import Admin from './admin';
import List from './list';
import ListFilm from './listfilm';
import MovieList from './movielist';

class App extends Component {

    constructor(){
        super()
        this.state = {
            choose: true,
            color: 'red'
        }

    }

    componentDidMount() {
        this.setState({choose: this.state.choose});
    }

    componentWillUnmount(){
        console.log('testupdate')
    }
    
    test(x){
        console.log(x);
        if (x === true) console.log(x);
        this.setState({choose: x});
    }
    
    invert(x){
        console.log(x);
        if (x === true) console.log(x);
        this.setState({choose: x});
    }   

    render(){
        let { user }  = this.props.match.params;
        const { choose, color } = this.state;
        let x = true;
        let b = false;

        return (
        <div style={{background: 'black'}}>
            <Admin invertX={() => this.invert(b)} choose={choose} userX={user} pathX={() => this.test(x)} colorX={color}/>
            {
                choose ? (<List />) : (
                    <div> 
                        <div className="pb-5">
                            <ListFilm />
                        </div>
                        <div>
                            <MovieList />
                        </div>
                    </div>
                )
            }
        </div>
        );
    }
}

export default App;
